import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function HomePage() {
  const navigate = useNavigate();
  const [userRole, setUserRole] = useState("Guest"); // Default role

  const handleRestrictedAccess = () => {
    alert("Unauthorized! You do not have access to this feature.");
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-b from-gray-900 to-black text-white">
      <div className="w-full max-w-md p-6 bg-gray-800 rounded-2xl shadow-xl text-center">
        <h1 className="text-3xl font-bold mb-4">Welcome, {userRole}!</h1>
        <p className="text-gray-400 mb-6">
          You are currently assigned the role of <span className="font-semibold text-yellow-400">{userRole}</span>.
        </p>
        
        <button
          onClick={handleRestrictedAccess}
          className="w-full bg-red-500 hover:bg-red-600 text-white font-bold py-2 rounded-xl mb-4"
        >
          Access Restricted Feature
        </button>

        <p className="text-gray-400">
          Don't have an account?{" "}
          <span
            className="text-yellow-400 cursor-pointer"
            onClick={() => navigate("/signup")}
          >
            Sign Up
          </span>
        </p>
      </div>
    </div>
  );
}
