import { useState, useEffect } from "react";

export default function Home() {
  

  // Simulating fetching user role (Replace with API call if needed)
  useEffect(() => {
    const storedRole = localStorage.getItem("userRole") || "guest"; // Default role: guest
    setRole(storedRole);
  }, []);

  const handleRestrictedAccess = () => {
    if (role === "admin") {
      setMessage("Access granted! Welcome to the admin panel.");
    } else {
      setMessage("Unauthorized: You do not have permission to access this feature.");
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white">
      <div className="p-6 bg-gray-800 rounded-xl shadow-lg text-center">
        <h1 className="text-3xl font-bold">Welcome to the Home Page</h1>
        <p className="mt-3 text-lg">Your role: <span className="text-yellow-400 font-semibold">{role}</span></p>

        {/* Restricted Feature Button */}
        <button
          onClick={handleRestrictedAccess}
          className="mt-5 px-4 py-2 bg-yellow-400 hover:bg-yellow-500 text-black font-bold rounded-lg"
        >
          Access Restricted Feature
        </button>

        {/* Unauthorized Message */}
        {message && <p className="mt-4 text-red-500">{message}</p>}
      </div>
    </div>
  );
}
